<template>
  <CSidebar
    fixed
    :minimize="minimize"
    :show="show"
    @update:show="(value) => $store.commit('set', ['sidebarShow', value])"
  >
    <CSidebarBrand class="d-md-down-none" to="/">
      <CIcon
        class="c-sidebar-brand-full"
        src="data:image/png;base64,"
        size="custom-size"
        :height="35"
        viewBox="0 0 556 134"
      />
      <CIcon
        class="c-sidebar-brand-minimized"
        name="logo"
        size="custom-size"
        :height="35"
        viewBox="0 0 110 134"
      />
    </CSidebarBrand>

    <CRenderFunction :key="RFKey" flat :content-to-render="$options.nav" />
    <CSidebarMinimizer
      class="d-md-down-none"
      @click.native="$store.commit('set', ['sidebarMinimize', !minimize])"
    />
  </CSidebar>
</template>

<script>
import nav from "./_nav";
import services from "../services/factory";

export default {
  name: "TheSidebar",
  nav,
  created() {
    this.init();
  },
  computed: {
    show() {
      return this.$store.state.sidebarShow;
    },
    minimize() {
      return this.$store.state.sidebarMinimize;
    },
    dashboard() {
      return {
        _name: 'CSidebarNavItem',
        name: 'Home Page',
        to: '/dashboard',
        icon: 'cil-home',
      }
    },
  },
  methods: {
    async init() {
      const books = await this.fetchDocument();
      const system = await this.fetchSystem();
      const operating = await this.fetchOperating();
      this.$options.nav[0]._children = [this.dashboard , ...books, ...operating, ...system];
      this.RFKey += 1;
    },

    async fetchDocument() {
      return await services.book.all().then(response => {
        return [
          {
            _name: "CSidebarNavTitle",
            _children: ["Documents"]
          },
          ...response.data.map(item => {
            return {
              _name: "CSidebarNavItem",
              name: item.name,
              to: `/books/${item.id}/documents`,
              icon: "cil-notes"
            };
          })
        ];
      });
    },

    async fetchSystem() {
      return await services.auth.namePermissions().then(permissions => {
        return this.system.filter(item => {
          return !!item.permission
            ? permissions.includes(item.permission)
            : true;
        });
      });
    },

    async fetchOperating() {
      return await services.auth.namePermissions().then(permissions => {
        return this.operating.filter(item => {
          return !!item.permission
            ? permissions.includes(item.permission)
            : true;
        });
      });
    }
  },
  data() {
    return {
      RFKey: 0, // key for rerender sidebar
      operating: [
         {
           _name: "CSidebarNavTitle",
           _children: ["Permission"]
         },
         {
           _name: "CSidebarNavItem",
           name: "Statistical",
           to: "/statistic",
           icon: "cil-notes",
           permission: "Statistical"
         }
       ],
       system:[
         {
           _name: "CSidebarNavTitle",
           _children: ["System"]
         },
         {
           _name: "CSidebarNavItem",
           name: "User",
           to: "/users",
           icon: "cil-people",
           permission: "Permission user"
         },
         {
           _name: "CSidebarNavItem",
           name: "Position",
           to: "/titles",
           icon: "cil-contact",
           permission: "Permission position"
         },
         {
           _name: "CSidebarNavItem",
           name: "Department",
           to: "/departments",
           icon: "cil-lan",
           permission: "permission subdivision"
         },
         {
           _name: "CSidebarNavItem",
           name: "Signed",
           to: "/signers",
           icon: "cil-touch-app",
           permission: "Permission signed"
         },
         {
           _name: "CSidebarNavItem",
           name: "Place of issue",
           to: "/publishers",
           icon: "cil-institution",
           permission: "Permission issuing place"
         },
         {
           _name: "CSidebarNavItem",
           name: "Document type",
           to: "/document-types",
           icon: "cil-text-size",
           permission: "Permission document view"
         },
         {
           _name: "CSidebarNavItem",
           name: "Documents",
           to: "/books",
           icon: "cil-book",
           permission: "Book permission"
         },
         {
           _name: "CSidebarNavItem",
           name: "Group",
           to: "/roles",
           icon: "cil-address-book",
           permission: "permission group"
         },
         {
           _name: "CSidebarNavItem",
           name: "Permission",
           to: "/permissions",
           icon: "cil-lock-locked",
           permission: "Permission permission"
         },
         {
           _name: "CSidebarNavItem",
           name: "Instruction",
           to: "/help",
           icon: "cil-touch-app"
         },
         {
           _name: "CSidebarNavItem",
           name: "Settings",
           to: "/settings",
           icon: "cil-settings"
         }
       ]
    };
  }
};
</script>
